package bridgeclass;

public class Alphabetchecker {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int a = 5, b = 10;
         int c, d;
         c = a++ + ++b ;
         d = --b + c-- ;
         
         System.out.println(a);
         System.out.println(b);
         System.out.println(c);
         System.out.println(d);
	}
    

}
